import MovieCard from "./MovieCard";
import movies from "../data/Movie";
import "./MovieList.css";
import { useState } from "react";

function MovieList({ searchTerm }) {
  const [select, setSelect] = useState(null); // ŸáŸÖŸàŸÜ state ⁄©Ÿá ÿØÿßÿ¥ÿ™€åÿå ŸÅŸÇÿ∑ ÿßÿ≥ÿ™ŸÅÿßÿØŸá‚Äåÿßÿ¥ ÿ®Ÿáÿ™ÿ± ÿ¥ÿØŸá

  const filteredMovies = movies.filter((movie) =>
    movie.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const clickhandler = (movie) => {
    setSelect(movie); // ŸàŸÇÿ™€å ÿ±Ÿà€å ŸÅ€åŸÑŸÖ ⁄©ŸÑ€å⁄© ÿ¥ÿØÿå ÿßŸàŸÜ ŸÅ€åŸÑŸÖ ÿßŸÜÿ™ÿÆÿßÿ® ÿ®ÿ¥Ÿá
  };

  const closeModal = () => {
    setSelect(null); // ÿ®ÿ±ÿß€å ÿ®ÿ≥ÿ™ŸÜ ŸÖŸàÿØÿßŸÑ
  };

  return (
    <div className="movie-list">
      {filteredMovies.map((movie) => (
        <div key={movie.id} onClick={() => clickhandler(movie)}>
          <MovieCard {...movie} />
        </div>
      ))}

      
      {select && (
        <div className="modal" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img
              src={select.posterUrl}
              alt={select.title}
              className="modal-poster"
            />
            <h2>{select.title} ({select.year})</h2>
            <p>{select.shortPlot}</p>
            <p><strong>Rating:</strong> {select.rating}</p>
            <a href={select.downloadUr} className="download-btn">üì• ÿØÿßŸÜŸÑŸàÿØ ŸÅ€åŸÑŸÖ</a>
            <button className="close-btn" onClick={closeModal}>ÿ®ÿ≥ÿ™ŸÜ ‚ùå</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default MovieList;
